function TchatGetMessageChannel(a,b)MySQL.Async.fetchAll("SELECT * FROM phone_app_chat WHERE channel = @channel ORDER BY time DESC LIMIT 100",{['@channel']=a},b)end;function TchatAddMessage(a,c)local d="INSERT INTO phone_app_chat (`channel`,`message`) VALUES(@channel,@message);"local e="SELECT * from phone_app_chat WHERE `id` = @id;"local f={['@channel']=a,['@message']=c}MySQL.Async.insert(d,f,function(g)MySQL.Async.fetchAll(e,{['@id']=g},function(h)TriggerClientEvent('gcPhone:tchat_receive',-1,h[1])end)end)end;RegisterServerEvent('gcPhone:tchat_channel')AddEventHandler('gcPhone:tchat_channel',function(a)local i=tonumber(source)TchatGetMessageChannel(a,function(j)TriggerClientEvent('gcPhone:tchat_channel',i,a,j)end)end)RegisterServerEvent('gcPhone:tchat_addMessage')AddEventHandler('gcPhone:tchat_addMessage',function(a,c)TchatAddMessage(a,c)end)